package th.co.truemoney.product.api.manager;import static org.junit.Assert.assertEquals;import static org.junit.Assert.assertNotNull;import java.util.ArrayList;import java.util.Iterator;import java.util.List;import org.junit.Test;import org.junit.runner.RunWith;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.context.annotation.Profile;import org.springframework.test.context.ContextConfiguration;import org.springframework.test.context.junit4.SpringJUnit4ClassRunner;import org.springframework.test.context.web.WebAppConfiguration;import th.co.truemoney.product.api.config.TestWebConfig;@RunWith(SpringJUnit4ClassRunner.class)@WebAppConfiguration@ContextConfiguration(classes = { TestWebConfig.class })public class TestBillConfigurationManager {		@Autowired	private BillConfigurationManager configuration;		@Autowired	private BillCategoryConfigurationManager categoryConfiguration;		@Test	public void testLoadConfigFile() {		BillConfiguration conf = configuration.getBillReference("tisco");		assertEquals("ธนาคารทิสโก้", conf.getTitleTh());	}		@Test	public void testLoadConfigCategory() {		ArrayList<BillCategoryConfiguration> billCategoryConfigurations = categoryConfiguration.getBillCategoryConfigurations();		for (BillCategoryConfiguration billCategoryConfiguration : billCategoryConfigurations) {			List<BillInformation> billInformations = (List<BillInformation>) billCategoryConfiguration.getItems();			for (BillInformation billInformation : billInformations) {				assertNotNull(billInformation);			}		}	}}